package ClasesOperadores;

public class Ejercicio1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String nombreEstudiante ="David H Caycedo B";
		double n1 = 6.23;
		double n2 = 8.23;
		double n3 = 7.56;
		double n4 = 5.23;
		double n5 = 9.99;
		double pro=(n1+n2+n3+n4+n5)/5.00;
		System.out.println("El estudiante " + nombreEstudiante+" tiene las  sigueinetes notas :");
		System.out.println("Nota 1 : " + n1);
		System.out.println("Nota 2 : " + n2);
		System.out.println("Nota 3 : " + n3);
		System.out.println("Nota 4 : " + n4);
		System.out.println("Nota 5 : " + n5);
		System.out.println("El promedioa es : " + pro);
	}
}
