package ClasesOperadores;

public class Ejercicio2 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int v1 =9;
		int v2 = 7;
		System.out.println(v1 + " + "+ v2 +" = "+(v1+v2));
		System.out.println(v1 + " - "+ v2 +" = "+(v1-v2));
		System.out.println(v1 + " * "+ v2 +" = "+(v1*v2));
		System.out.println(v1 + " / "+ v2 +" = "+(v1+v2));
		System.out.println(v1 + " % "+ v2 +" = "+(v1%v2));
	}
}
